DROP TABLE IF EXISTS `sys_user`;
CREATE TABLE `sys_user` (`user_id` BIGINT NOT NULL AUTO_INCREMENT COMMENT 'ID', PRIMARY KEY (`user_id`), `gmt_create` DATETIME COMMENT '创建时间', `gmt_modified` DATETIME COMMENT '最后一次更新时间', `name` VARCHAR(32) COMMENT '姓名', `phone` CHAR(11) COMMENT '手机', `email` VARCHAR(128) COMMENT '邮箱', `telephone` VARCHAR(16) COMMENT '电话', `address` VARCHAR(64) COMMENT '地址', `username` VARCHAR(64) COMMENT '登录名', `password` VARCHAR(64) COMMENT '密码', `remark` VARCHAR(255) COMMENT '备注', `wx_openid` VARCHAR(32) COMMENT '微信openid', `app_id` CHAR(32) COMMENT '应用id', `secret_key` CHAR(64) COMMENT '密钥', `salt` VARCHAR(32) COMMENT '密码盐', `is_admin` TINYINT DEFAULT '0' COMMENT '是否是管理员') ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户表';
DROP TABLE IF EXISTS `sys_role`;
CREATE TABLE `sys_role` (`role_id` BIGINT NOT NULL AUTO_INCREMENT COMMENT 'ID', PRIMARY KEY (`role_id`), `gmt_create` DATETIME COMMENT '创建时间', `gmt_modified` DATETIME COMMENT '最后一次更新时间', `role_name` VARCHAR(32) COMMENT '角色名称', `role_flag` VARCHAR(32) COMMENT '角色标识') ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='角色表';
DROP TABLE IF EXISTS `sys_user_role`;
CREATE TABLE `sys_user_role` (`user_role_id` BIGINT NOT NULL AUTO_INCREMENT COMMENT 'ID', PRIMARY KEY (`user_role_id`), `gmt_create` DATETIME COMMENT '创建时间', `gmt_modified` DATETIME COMMENT '最后一次更新时间', `user_id` BIGINT COMMENT '用户id', `role_id` BIGINT COMMENT '角色id') ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户角色映射表';
DROP TABLE IF EXISTS `sys_permission`;
CREATE TABLE `sys_permission` (`permission_id` BIGINT NOT NULL AUTO_INCREMENT COMMENT 'ID', PRIMARY KEY (`permission_id`), `gmt_create` DATETIME COMMENT '创建时间', `gmt_modified` DATETIME COMMENT '最后一次更新时间', `permission_flag` VARCHAR(50) COMMENT '权限标识', `permission_name` VARCHAR(32) COMMENT '权限名称', `type` CHAR(2) COMMENT '00:菜单权限；01：接口url权限', `object_id` BIGINT COMMENT '对应表id（菜单权限即为菜单id）', `parent_id` BIGINT COMMENT '父权限id（permission表中的id值）', `data_path` VARCHAR(512) COMMENT '数据库id地址', `sort` INT DEFAULT '1' COMMENT '排序值') ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='权限表';
DROP TABLE IF EXISTS `sys_role_permission`;
CREATE TABLE `sys_role_permission` (`role_permission_id` BIGINT NOT NULL AUTO_INCREMENT COMMENT 'ID', PRIMARY KEY (`role_permission_id`), `gmt_create` DATETIME COMMENT '创建时间', `gmt_modified` DATETIME COMMENT '最后一次更新时间', `permission_id` BIGINT COMMENT '权限id', `role_id` BIGINT COMMENT '角色id') ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='角色权限表';
DROP TABLE IF EXISTS `sys_menu`;
CREATE TABLE `sys_menu` (`menu_id` BIGINT NOT NULL AUTO_INCREMENT COMMENT 'ID', PRIMARY KEY (`menu_id`), `gmt_create` DATETIME COMMENT '创建时间', `gmt_modified` DATETIME COMMENT '最后一次更新时间', `name` VARCHAR(64) COMMENT '菜单名称', `path` VARCHAR(128) COMMENT '菜单地址', `component` VARCHAR(128) COMMENT '视图文件路径', `redirect` VARCHAR(128) COMMENT '跳转地址（如果设置为noredirect会在面包屑导航中无连接）', `title` VARCHAR(64) COMMENT '菜单显示名称', `icon` VARCHAR(64) COMMENT '菜单图标', `hidden` TINYINT(2) COMMENT '1:true,0:false如果设置true，会在导航中隐藏', `always_show` TINYINT(2) COMMENT '1:true,0:false没有子菜单也会显示在导航中') ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='菜单表';
DROP TABLE IF EXISTS `sys_base`;
CREATE TABLE `sys_base` (`base_id` BIGINT NOT NULL AUTO_INCREMENT COMMENT 'ID', PRIMARY KEY (`base_id`), `gmt_create` DATETIME COMMENT '创建时间', `gmt_modified` DATETIME COMMENT '最后一次更新时间', `table_name` VARCHAR(64) COMMENT '表名', `table_remark` VARCHAR(128) COMMENT '表备注', `field_name` VARCHAR(64) COMMENT '字段名', `field_type` VARCHAR(32) COMMENT '字段类型', `field_length` INT COMMENT '字段长度', `is_null` TINYINT COMMENT '1:是，0：否；是否可以为空', `is_unique` TINYINT COMMENT '1:是，0：否；是否唯一', `is_unsigned` TINYINT COMMENT '1:是，0：否；是否为无符号', `default_value` VARCHAR(128) COMMENT '默认值', `field_remark` VARCHAR(255) COMMENT '字段备注', `is_id` TINYINT COMMENT '1:是，0：否；是否为id', `modules` VARCHAR(50) COMMENT '模块名称') ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='数据库基础表，包含所有数据库信息';